<?phpnamespace App;use Illuminate\Notifications\Notifiable;use Illuminate\Foundation\Auth\User as Authenticatable;class User extends Authenticatable{    use Notifiable;    /**     * The attributes that are mass assignable.     *     * @var array     */    protected $fillable = [        'role_id',  'parent_id', 'name', 'email', 'email_varif', 'bonus',  'password', 'surname', 'phone', 'adress', 'real_adress', 'tax_number','company', 'banking_number', 'status'    ];    /**     * The attributes that should be hidden for arrays.     *     * @var array     */    protected $hidden = [        'password', 'remember_token',    ];    public function role()    {        return $this->belongsTo(Role::class);    }    public function orders()    {        return $this->hasMany(Order::class);    }    public function referals()    {        return $this->hasMany(Referal::class);    }    public function isAdmin()    {        if ($this->role->name === 'Admin') {            return true;        }    }}